	public static int getNumberOfVoicesInDashboard(String requestBody) {
		int count = 0;

		String authToken = AUTH_TOKEN;
		// System.out.println(authToken);

		if (authToken == null) {
			System.out.println("❌ Error: No Auth Token Found!");
			return 0;
		}

//		.header("Referer",
//				"https://dev.botminds.ai/VOCTest/Voice%2520of%2520Customer%2520/_dashboard?did=1d7878dd511dd173d7a7947c73227ea4")
		HttpRequest request = HttpRequest.newBuilder().uri(URI.create(widgetUrl))
				.header("Content-Type", "application/json").header("Authorization", AUTH_TOKEN)
				
				.header("timezone", "Asia/Calcutta").header("subscriptionId", Subs_Id).header("finonId", Finon_Id)
				.header("User-Agent",
						"Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/134.0.0.0 Safari/537.36 Edg/134.0.0.0")
				.POST(HttpRequest.BodyPublishers.ofString(requestBody)).build();

		try {
			HttpResponse<String> response = client.send(request, HttpResponse.BodyHandlers.ofString());

			// System.out.println("Status Code "+response.statusCode());
			// System.out.println(response.body());
			if (response.statusCode() == 200) {
				// System.out.println(response.body());
				String responseBody = response.body();
				JSONObject responseJson = new JSONObject(responseBody);
				if (responseJson.has("Result") && !responseJson.isNull("Result")) {
					Object resultObj = responseJson.get("Result");

					if (resultObj instanceof JSONArray) {
						JSONArray resultArray = responseJson.getJSONArray("Result");

						if (resultArray.length() > 0) {
							JSONObject result = resultArray.getJSONObject(0);
							String name = result.getString("Name");
							count = result.getInt("Count");

//							System.out.println("✅ Extracted Data:");
//							System.out.println("Name: " + name);
//							System.out.println("Count: " + count);
						} else {
							System.out.println("❌ 'Result' array is empty!");
						}
					} else {
						System.out.println("⚠️ Failed to fetch data. Status Code: " + response.statusCode());
					}
				}
			}
		} catch (IOException | InterruptedException e) {
			e.printStackTrace();
		}
		return count;
	}

	public static String getAuthTokenFromLogin() {
		String accessToken = null;

		String requestBody = "{\"grant_type\":\"password\",\"UserName\":\"voctest@botminds.ai\",\"Password\":\"\",\"RememberMe\":false}";
		HttpRequest request = HttpRequest.newBuilder().uri(URI.create(loginUrl))
				.header("Content-Type", "application/json").header("Authorization", "Bearer")
				.header("Referer", "https://dev.botminds.ai/profile/login").header("timezone", "Asia/Calcutta")
				.header("subscriptionId", Subs_Id).header("finonId", "")
				.header("User-Agent",
						"Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/134.0.0.0 Safari/537.36 Edg/134.0.0.0")
				.POST(HttpRequest.BodyPublishers.ofString(requestBody)).build();
		try {
			HttpResponse<String> response = client.send(request, HttpResponse.BodyHandlers.ofString());

			if (response.statusCode() == 200) {
				String responseBody = response.body();
				JSONObject responseJson = new JSONObject(responseBody);
				accessToken = responseJson.getString("AccessToken");
			}
		} catch (IOException | InterruptedException e) {
			e.printStackTrace();
		}
		System.out.println("Access Token: " + accessToken);
		return accessToken;
	}

