	public String getSubscriptionId(String subsName) {
		String authToken = AUTH_TOKEN;

		if (authToken == null) {
			System.out.println("❌ Error: No Auth Token Found!");
			// return 0;
		}

		String subscriptionId = "";
		HttpRequest request = HttpRequest.newBuilder().uri(URI.create(subsUrl))
				.header("Content-Type", "application/json").header("Authorization", AUTH_TOKEN)
				.header("Referer", "https://dev.botminds.ai/" + subsName).header("timezone", "Asia/Calcutta")
				.header("User-Agent",
						"Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/134.0.0.0 Safari/537.36 Edg/134.0.0.0")
				.GET().build();

		try {
			HttpResponse<String> response = client.send(request, HttpResponse.BodyHandlers.ofString());

			if (response.statusCode() == 200) {
				String responseBody = response.body();
				JSONObject responseJson = new JSONObject(responseBody);

				if (responseJson.has("Result") && !responseJson.isNull("Result")) {
					Object resultObj = responseJson.get("Result");

					if (resultObj instanceof JSONArray) {
						JSONArray resultArray = (JSONArray) resultObj;

						for (int i = 0; i < resultArray.length(); i++) {
							JSONObject result = resultArray.getJSONObject(i);
							subscriptionId = result.getString("Id");
						}
					}
				}
			}
		} catch (IOException | InterruptedException e) {
			e.printStackTrace();
		}
		return subscriptionId;
	}

	

	public String getFinonId(String subsName, String subsId, String projectName) {
		String authToken = AUTH_TOKEN;
		String encodedProjectName=null;
		try {
			encodedProjectName=URLEncoder.encode(projectName, "UTF-8");
			encodedProjectName=encodedProjectName.replace("+", "%20");
			System.out.println("Encoded Project Name : "+encodedProjectName);
		}catch(UnsupportedEncodingException e){
			e.printStackTrace();
		}

		finonUrl=finonUrl+subsName+"/"+encodedProjectName;
		
		if (authToken == null) {
			System.out.println("❌ Error: No Auth Token Found!");
			//return 0;
		}
		
		String finond="";
		HttpRequest request = HttpRequest.newBuilder().uri(URI.create(finonUrl))
				.header("Content-Type", "application/json").header("Authorization", AUTH_TOKEN)
				.header("Referer",
						"https://dev.botminds.ai/"+subsName)
				.header("timezone", "Asia/Calcutta").header("subscriptionId", subsId)
				.header("User-Agent",
						"Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/134.0.0.0 Safari/537.36 Edg/134.0.0.0")
				.GET().build();
		
		try {
			HttpResponse<String> response = client.send(request, HttpResponse.BodyHandlers.ofString());
			
			if (response.statusCode() == 200) {
				String responseBody = response.body();
				JSONObject responseJson = new JSONObject(responseBody);
				
				if (responseJson.has("FinonId")) {
					Object resultObj = responseJson.get("FinonId");
					finond=resultObj.toString();
				}
			}
		} catch (IOException | InterruptedException e) {
			e.printStackTrace();
		}
		System.out.println("Finon Id Fetched Successfully : "+finond);
		return finond;
	}

